namespace Harvester.Core.Tests.Messaging.Sources.DbWin.UsingOutputDebugStringWriter
{
    public class WhenWritingMessage : IDisposable
    {
        private readonly Mock<MessageBuffer> messageBuffer = new Mock<MessageBuffer>("BufferName");
        private readonly ManualResetEvent setupComplete = new ManualResetEvent(false);
        private readonly ManualResetEvent testComplete = new ManualResetEvent(false);
        private readonly OutputDebugStringWriter messageWriter;
        private readonly String mutexName;

        public WhenWritingMessage()
        {
            mutexName = "Harvester: " + Guid.NewGuid();
            messageWriter = new OutputDebugStringWriter(mutexName, messageBuffer.Object);
            messageBuffer.Object.Timeout = TimeSpan.FromMilliseconds(25);
        }

        public void Dispose()
        {
            setupComplete.Dispose();
            testComplete.Dispose();
        }

        [Fact]
        public void DoNotWriteMessageIfMutexCreated()
        {
            messageWriter.Write("My Message");

            messageBuffer.Verify(mock => mock.Write(It.IsAny<Byte[]>()), Times.Never());
        }

        [Fact]
        public void DoNotWriteMessageIfMutexWaitTimeout()
        {
            Task.Factory.StartNew(() =>
                                      {
                                          using (new Mutex(true, mutexName))
                                          {
                                              setupComplete.Set(); 
                                              testComplete.WaitOne();
                                          }
                                      });

            setupComplete.WaitOne();

            messageWriter.Write("My Message");

            messageBuffer.Verify(mock => mock.Write(It.IsAny<Byte[]>()), Times.Never());

            testComplete.Set();
        }

        [Fact]
        public void AlwaysWriteMessageWithProcessIdPreamble()
        {
            using (new Mutex(false, mutexName))
            using (var process = Process.GetCurrentProcess())
            {
                var processId = process.Id;

                messageWriter.Write("My First Message");
                messageWriter.Write("My Second Message");

                messageBuffer.Verify(mock => mock.Write(It.Is<Byte[]>(buffer => BitConverter.ToInt32(buffer, 0) == processId)), Times.Exactly(2));
            }
        }

        [
        Theory,
        InlineData("", "2"),
        InlineData("", "23")
        ]
        public void ChunkMessageIfExceedsBufferSize(String messageChunk1, String messageChunk2)
        {
            using (new Mutex(false, mutexName))
            {
                var chunks = new Queue<String>();

                messageBuffer.Setup(mock => mock.Write(It.IsAny<Byte[]>())).Callback((Byte[] buffer) => chunks.Enqueue(new OutputDebugString("Source", buffer).Message));

                messageWriter.Write(messageChunk1 + messageChunk2);

                Assert.Equal(messageChunk1, chunks.Dequeue());
                Assert.Equal(messageChunk2, chunks.Dequeue());
            }
        }

        [
        Theory,
        InlineData(null, 5),
        InlineData("", 5),
        InlineData("Small Message", 17),
        InlineData("", 4095),
        InlineData("", 4095),
        InlineData("", 4095)
        ]
        public void AlwaysTerminateMessageWithNullByte(String message, Int32 expectedNullByteOffset)
        {
            using (new Mutex(false, mutexName))
            {
                var chunks = new Queue<Byte[]>();

                messageBuffer.Setup(mock => mock.Write(It.IsAny<Byte[]>())).Callback((Byte[] buffer) => chunks.Enqueue(buffer));

                messageWriter.Write(message);

                Assert.Equal(0, chunks.Dequeue()[expectedNullByteOffset]);
            }
        }

        [Theory, InlineData(null, 0), InlineData("", 0), InlineData("Small Message", 13)]
        public void SingleMessageIfLessThanBufferSize(String message, Int32 length)
        {
            using (new Mutex(false, mutexName))
            {
                var chunks = new Queue<String>();

                messageBuffer.Setup(mock => mock.Write(It.IsAny<Byte[]>())).Callback((Byte[] buffer) => chunks.Enqueue(new OutputDebugString("Source", buffer).Message));

                messageWriter.Write(message);

                Assert.Equal(1, chunks.Count);
                Assert.Equal(message ?? String.Empty, chunks.Dequeue());
            }
        }
    }
}
